import React from "react";

function App() {
  const pedirPizza = async (pizza) => {
    const res = await fetch("http://localhost:5000/pedido", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ pizza })
    });
    const data = await res.json();
    alert(data.status);
  };

  return (
    <div style={{ textAlign: "center", marginTop: "50px" }}>
      <h1>üçï Pizzaria</h1>
      <button onClick={() => pedirPizza("Mussarela")}>Mussarela</button>
      <button onClick={() => pedirPizza("Calabresa")}>Calabresa</button>
      <button onClick={() => pedirPizza("Portuguesa")}>Portuguesa</button>
    </div>
  );
}

export default App;
